import { all, call } from 'redux-saga/effects';
import {fetchCollectionsStart} from './shop/shop.sagas';
import {onGoogleSignInStart, onEmailSignInStart, onCheckUserSession, userSagas} from './user/user.sagas';
import {onSignOutSuccess, cartSagas} from './cart/cart.sagas';

export default function* rootSaga() {
    yield all([
        call(fetchCollectionsStart), 
        call(userSagas), 
        call(cartSagas)
    ])
};
